import unittest
from day1_code import get_target_floor, find_when_basement_reached


class Day1Part1Tests(unittest.TestCase):

    def test_left_bracket_goes_up_a_floor(self):
        result = get_target_floor('(')
        self.assertEqual(1, result)

    def test_right_bracket_goes_down_a_floor(self):
        result = get_target_floor(')')
        self.assertEqual(-1, result)

    def test_left_bracket_plus_right_bracket_returns_floor_zero(self):
        result = get_target_floor('()')
        self.assertEqual(0, result)

    def test_two_right_and_two_left_returns_floor_zero(self):
        result = get_target_floor('()()')
        self.assertEqual(0, result)

    def test_go_up_three_floors(self):
        result = get_target_floor('(((')
        self.assertEqual(3, result)

    def test_should_result_on_floor_three(self):
        result = get_target_floor('(()(()(')
        self.assertEqual(3, result)

    def test_should_also_result_on_floor_three(self):
        result = get_target_floor('))(((((')
        self.assertEqual(3, result)

    def test_should_result_on_floor_neg_one_aka_basement(self):
        result = get_target_floor('())')
        self.assertEqual(-1, result)

    def test_should_also_result_on_floor_neg_one_aka_basement(self):
        result = get_target_floor('))(')
        self.assertEqual(-1, result)

    def test_should_also_result_on_floor_neg_three_aka_basement(self):
        result = get_target_floor(')))')
        self.assertEqual(-3, result)

    def test_should_also_result_on_floor_neg_three_aka_basement(self):
        result = get_target_floor(')())())')
        self.assertEqual(-3, result)

